// src/hooks/useBufferState.perf.test.ts
import { renderHook, act } from '@testing-library/react-hooks';
import { useBufferState } from './useBufferState';

describe('useBufferState Performance', () => {
  const LARGE_ENTRY_COUNT = 10000;
  const ITERATIONS = 1000;
  const MAX_TIME_MS = 10; // Max time for operation

  function createLargeEntryList(count: number): Entry[] {
    return Array.from({ length: count }, (_, i) => ({
      id: `entry-${i}`,
      name: `file-${i}.txt`,
      type: 'file' as const,
      path: `/bucket/file-${i}.txt`,
      size: 1024,
      lastModified: new Date(),
    }));
  }

  it('handles cursor movement in O(1) time with large lists', () => {
    const entries = createLargeEntryList(LARGE_ENTRY_COUNT);
    const { result } = renderHook(() => useBufferState());

    act(() => {
      result.current.setEntries(entries);
    });

    const start = performance.now();
    for (let i = 0; i < ITERATIONS; i++) {
      act(() => {
        result.current.moveCursor(1);
      });
    }
    const elapsed = performance.now() - start;
    const avgTime = elapsed / ITERATIONS;

    expect(avgTime).toBeLessThan(MAX_TIME_MS);
    console.log(`Average cursor move time: ${avgTime.toFixed(3)}ms`);
  });

  it('handles selection toggle in O(1) time', () => {
    const entries = createLargeEntryList(LARGE_ENTRY_COUNT);
    const { result } = renderHook(() => useBufferState());

    act(() => {
      result.current.setEntries(entries);
    });

    const start = performance.now();
    for (let i = 0; i < ITERATIONS; i++) {
      act(() => {
        result.current.toggleSelection(i % LARGE_ENTRY_COUNT);
      });
    }
    const elapsed = performance.now() - start;
    const avgTime = elapsed / ITERATIONS;

    expect(avgTime).toBeLessThan(MAX_TIME_MS);
  });

  it('handles undo/redo without memory leaks', () => {
    const entries = createLargeEntryList(100);
    const { result } = renderHook(() => useBufferState());

    act(() => {
      result.current.setEntries(entries);
    });

    // Perform many operations
    for (let i = 0; i < 1000; i++) {
      act(() => {
        result.current.moveCursor(1);
        result.current.toggleSelection(i % 100);
      });
    }

    // Check undo history is bounded
    // This would require exposing undo history size or checking memory
    expect(result.current.canUndo).toBe(true);
  });

  it('setEntries scales linearly with entry count', () => {
    const { result } = renderHook(() => useBufferState());

    const times: number[] = [];
    const sizes = [100, 1000, 5000, 10000];

    for (const size of sizes) {
      const entries = createLargeEntryList(size);
      const start = performance.now();
      act(() => {
        result.current.setEntries(entries);
      });
      times.push(performance.now() - start);
    }

    // Verify roughly linear scaling (10x entries should be ~10x time)
    const ratio = times[3] / times[0];
    expect(ratio).toBeLessThan(200); // Allow some variance

    console.log('setEntries scaling:', sizes.map((s, i) => `${s}: ${times[i].toFixed(2)}ms`));
  });
});
```

