### Step 2: Design BaseDialog Component
```typescript
// src/ui/base-dialog-react.tsx
import { Box, Text } from '@anthropic/opentui';

interface BaseDialogProps {
  visible: boolean;
  title?: string;
  width?: number | string;
  height?: number | string;
  onClose?: () => void;
  children: ReactNode;
  footer?: ReactNode;
  borderColor?: string;
  // Keyboard handling delegated to parent or useKeyboardHandler
}

export function BaseDialog({
  visible,
  title,
  width = 60,
  height,
  onClose,
  children,
  footer,
  borderColor = 'blue',
}: BaseDialogProps) {
  if (!visible) return null;

  return (
    <Box
      position="absolute"
      top="50%"
      left="50%"
      transform="translate(-50%, -50%)"
      width={width}
      height={height}
      borderStyle="round"
      borderColor={borderColor}
      flexDirection="column"
    >
      {title && (
        <Box borderBottom borderColor={borderColor} paddingX={1}>
          <Text bold>{title}</Text>
        </Box>
      )}

      <Box flexGrow={1} padding={1}>
        {children}
      </Box>

      {footer && (
        <Box borderTop borderColor={borderColor} paddingX={1}>
          {footer}
        </Box>
      )}
    </Box>
  );
}
```

