### Step 2: Create Progress Reducer
```typescript
// src/hooks/useProgressState.ts
const initialState: ProgressState = {
  visible: false,
  title: '',
  description: '',
  value: 0,
  currentFile: '',
  currentNum: 0,
  totalNum: 0,
  cancellable: true,
  cancelled: false,
};

function progressReducer(state: ProgressState, action: ProgressAction): ProgressState {
  switch (action.type) {
    case 'SHOW':
      return { ...initialState, visible: true, ...action.payload };
    case 'UPDATE':
      return { ...state, ...action.payload };
    case 'SET_FILE':
      return { 
        ...state, 
        currentFile: action.payload.file,
        currentNum: action.payload.num,
        value: state.totalNum > 0 ? (action.payload.num / state.totalNum) * 100 : 0
      };
    case 'INCREMENT':
      const newNum = state.currentNum + 1;
      return {
        ...state,
        currentNum: newNum,
        value: state.totalNum > 0 ? (newNum / state.totalNum) * 100 : 0
      };
    case 'CANCEL':
      return { ...state, cancelled: true };
    case 'HIDE':
      return { ...state, visible: false };
    case 'RESET':
      return initialState;
    default:
      return state;
  }
}
