```typescript
// src/hooks/useKeyboardDispatcher.ts
import { useCallback, useRef } from 'react';
import { EditMode } from '../types/edit-mode';
import { KeyboardKey, KeyAction } from '../types/keyboard';
import { defaultKeybindings } from '../ui/keybindings';

interface KeyboardDispatcherOptions {
  mode: EditMode;
  isDialogOpen: boolean;
  keybindings?: KeybindingMap;
}

export function useKeyboardDispatcher({
  mode,
  isDialogOpen,
  keybindings = defaultKeybindings,
}: KeyboardDispatcherOptions) {
  const actionHandlersRef = useRef<Map<KeyAction, () => void>>(new Map());

  // Register action handlers
  const registerAction = useCallback((action: KeyAction, handler: () => void) => {
    actionHandlersRef.current.set(action, handler);
    return () => actionHandlersRef.current.delete(action);
  }, []);

  // Main dispatch function
  const dispatch = useCallback((key: KeyboardKey) => {
    // Skip if dialog is handling input
    if (isDialogOpen) return false;

    // Get binding for current mode
    const modeBindings = keybindings[mode];
    const action = modeBindings?.get(keyToString(key));

    if (action) {
      const handler = actionHandlersRef.current.get(action);
      if (handler) {
        handler();
        return true;
      }
    }

    return false;
  }, [mode, isDialogOpen, keybindings]);

  return { dispatch, registerAction };
}

function keyToString(key: KeyboardKey): string {
  const parts: string[] = [];
  if (key.ctrl) parts.push('ctrl');
  if (key.meta) parts.push('meta');
  if (key.shift) parts.push('shift');
  parts.push(key.name);
  return parts.join('+');
}
```

