export function parseS3Error(error: unknown): S3OperationError {
  if (error instanceof S3ServiceException) {
    return new S3OperationError(
      error.message,
      error.name,
      error.$metadata.httpStatusCode,
      error
    );
  }

  if (error instanceof Error) {
    return new S3OperationError(error.message, 'UnknownError', undefined, error);
  }

  return new S3OperationError(String(error), 'UnknownError');
}

export function isNotFoundError(error: unknown): boolean {
  return error instanceof S3OperationError && 
    (error.code === 'NoSuchKey' || error.code === 'NotFound');
}
