// src/ui/error-boundary-react.tsx
import React, { Component, ErrorInfo, ReactNode } from 'react';
import { Box, Text } from '@anthropic/opentui';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
}

interface State {
  hasError: boolean;
  error: Error | null;
}

export class ErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error: Error): State {
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    // Log to file or external service
    console.error('Error caught by boundary:', error);
    console.error('Component stack:', errorInfo.componentStack);

    this.props.onError?.(error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      if (this.props.fallback) {
        return this.props.fallback;
      }

      return (
        <Box
          flexDirection="column"
          padding={1}
          borderStyle="round"
          borderColor="red"
        >
          <Text color="red" bold>Something went wrong</Text>
          <Text color="gray">{this.state.error?.message}</Text>
          <Box marginTop={1}>
            <Text dimColor>Press 'q' to quit or 'r' to retry</Text>
          </Box>
        </Box>
      );
    }

    return this.props.children;
  }
}
