```typescript
// Before:
const s3Adapter = adapter as any;
if (s3Adapter.setBucket) { ... }

// After - Option 1: Type guard
function isS3Adapter(adapter: Adapter): adapter is S3Adapter {
  return adapter.name === 's3';
}

if (isS3Adapter(adapter) && adapter.setBucket) { ... }

// After - Option 2: Extend interface
