```typescript
import { ProgressCallback, ProgressEvent } from '../adapter.js';

/**
 * Callback type for internal S3 operations (count-based)
 */
export type OperationProgressCallback = (
  count: number,
  total: number,
  currentItem: string
) => void;

/**
 * Callback type for byte-based progress
 */
export type ByteProgressCallback = (
  bytesTransferred: number,
  totalBytes: number,
  currentFile: string
) => void;

/**
 * Create a progress adapter that converts internal progress to ProgressEvent
 * 
 * @param operation - Operation name for the progress event
 * @param onProgress - External progress callback (optional)
 * @returns Internal progress callback or undefined
 */
export function createProgressAdapter(
  operation: string,
  onProgress?: ProgressCallback
): OperationProgressCallback | undefined {
  if (!onProgress) return undefined;

  return (count: number, total: number, currentItem: string) => {
    onProgress({
      operation,
      bytesTransferred: count,
      totalBytes: total,
      percentage: total > 0 ? Math.round((count / total) * 100) : 0,
      currentFile: currentItem,
    });
  };
}

/**
 * Create a byte-based progress adapter
 */
export function createByteProgressAdapter(
  operation: string,
  onProgress?: ProgressCallback
): ByteProgressCallback | undefined {
  if (!onProgress) return undefined;

  return (bytesTransferred: number, totalBytes: number, currentFile: string) => {
    onProgress({
      operation,
      bytesTransferred,
      totalBytes,
      percentage: totalBytes > 0 ? Math.round((bytesTransferred / totalBytes) * 100) : 0,
      currentFile,
    });
  };
}
```

