import { 
  CreateMultipartUploadCommand,
  UploadPartCommand,
  CompleteMultipartUploadCommand,
  AbortMultipartUploadCommand,
} from '@aws-sdk/client-s3';

export const MULTIPART_THRESHOLD = 5 * 1024 * 1024; // 5MB
export const PART_SIZE = 5 * 1024 * 1024;

export interface MultipartUploadOptions {
  bucket: string;
  key: string;
  body: Buffer;
  contentType?: string;
  onProgress?: (uploaded: number, total: number) => void;
}

export class MultipartUploader {
  constructor(private client: S3Client) {}

  async upload(options: MultipartUploadOptions): Promise<void> {
    const { bucket, key, body } = options;
    const totalParts = Math.ceil(body.length / PART_SIZE);

    // Create multipart upload
    const { UploadId } = await this.client.send(
      new CreateMultipartUploadCommand({
        Bucket: bucket,
        Key: key,
        ContentType: options.contentType,
      })
    );

    try {
      const parts = await this.uploadParts(bucket, key, UploadId!, body, options.onProgress);
      await this.completeUpload(bucket, key, UploadId!, parts);
    } catch (error) {
      await this.abortUpload(bucket, key, UploadId!);
      throw error;
    }
  }

  // ... private methods
}
