### Step 3: Create Recovery Mechanisms
```typescript
// src/ui/error-recovery.tsx
interface RecoverableErrorBoundaryProps {
  children: ReactNode;
  onReset?: () => void;
}

export function RecoverableErrorBoundary({ 
  children, 
  onReset 
}: RecoverableErrorBoundaryProps) {
  const [key, setKey] = useState(0);

  const handleRetry = useCallback(() => {
    onReset?.();
    setKey(k => k + 1);
  }, [onReset]);

  return (
    <ErrorBoundary
      key={key}
      fallback={
        <ErrorFallbackWithRetry onRetry={handleRetry} />
      }
    >
      {children}
    </ErrorBoundary>
  );
}

function ErrorFallbackWithRetry({ onRetry }: { onRetry: () => void }) {
  useInput((input) => {
    if (input === 'r') {
      onRetry();
    }
  });

  return (
    <Box flexDirection="column" padding={1}>
      <Text color="red">An error occurred</Text>
      <Text dimColor>Press 'r' to retry</Text>
    </Box>
  );
}
```

