export type DialogAction =
  | { type: 'SHOW_CONFIRM'; payload: ConfirmDialogPayload }
  | { type: 'SHOW_HELP' }
  | { type: 'SHOW_SORT' }
  | { type: 'SHOW_UPLOAD' }
  | { type: 'SHOW_ERROR'; payload: ErrorDialogPayload }
  | { type: 'CLOSE' }
  | { type: 'CLOSE_ALL' };
