export function useDialogState() {
  const [state, dispatch] = useReducer(dialogReducer, initialState);

  const showConfirm = useCallback((payload: ConfirmDialogPayload) => {
    dispatch({ type: 'SHOW_CONFIRM', payload });
  }, []);

  const showError = useCallback((message: string, details?: string) => {
    dispatch({ type: 'SHOW_ERROR', payload: { message, details } });
  }, []);

  const showHelp = useCallback(() => dispatch({ type: 'SHOW_HELP' }), []);
  const showSort = useCallback(() => dispatch({ type: 'SHOW_SORT' }), []);
  const showUpload = useCallback(() => dispatch({ type: 'SHOW_UPLOAD' }), []);
  const closeDialog = useCallback(() => dispatch({ type: 'CLOSE' }), []);

  // Computed properties
