  const isConfirmOpen = state.activeDialog === 'confirm';
  const isHelpOpen = state.activeDialog === 'help';
  // etc.

  return {
    dialog: state,
    isDialogOpen,
    showConfirm,
    showError,
    showHelp,
    showSort,
    showUpload,
    closeDialog,
    dispatch,
  };
}
